import { Component, OnInit } from '@angular/core';
import {
    FormBuilder,
    FormControl,
    FormGroup,
    Validators,
} from '@angular/forms';
import { Router } from '@angular/router';
import { RxState } from '@rx-angular/state';
import { Message, MessageService } from 'primeng/api';
import {
    catchError,
    filter,
    mergeMap,
    Observable,
    of,
    Subject,
    tap,
} from 'rxjs';
import { AuthenService } from 'src/app/Services/authen.service';
import { UserService } from 'src/app/Services/user.service';
import { LoginState } from '../state';

@Component({
    selector: 'app-login',
    templateUrl: './login.component.html',
    styles: [
        `
            :host ::ng-deep .pi-eye,
            :host ::ng-deep .pi-eye-slash {
                transform: scale(1.6);
                margin-right: 1rem;
                color: var(--primary-color) !important;
            }
        `,
    ],
    providers: [RxState, MessageService],
})
export class LoginComponent implements OnInit {
    valCheck: string[] = ['remember'];
    msgs: Message[] = [];

    loginForm!: FormGroup;
    loading: boolean = false;
    onSubmit = new Subject<void>();
    onSubmitHandler$ = new Subject<{ userName: string; password: string }>();

    get state$(): Observable<LoginState> {
        return this._state.select();
    }

    get loading$():Observable<boolean>{
        return this._state.select('loading');
    }

    constructor(
        private _userService: UserService,
        private _authService: AuthenService,
        private _router: Router,
        private formBuilder: FormBuilder,
        private _state: RxState<LoginState>
    ) {
        this._state.set({ hasError: false , loading:false});
        this.msgs = [
            {
                severity: 'error',
                summary: 'Đăng nhập thất bại',
                detail: 'Tên đăng nhập hoặc mật khẩu không hợp lệ',
            },
        ];
    }

    initForm(): void {
        this.loginForm = this.formBuilder.group({
            userName: ['', [Validators.required]],
            password: ['', [Validators.required]],
        });
    }

    private manageEvents() {
        this._state.hold(this.onSubmit, () => {
            const valid = this.loginForm.valid;
            this._state.set({
                hasError: !valid,
            });
            if (!valid) {
                return;
            }
            this._state.set({loading:true});
            this.onSubmitHandler$.next({
                userName: this.loginForm.controls['userName'].value,
                password: this.loginForm.controls['password'].value,
            });
        });
    }

    private connectState(): void {
        const handler$ = this.onSubmitHandler$.pipe(
            mergeMap((data) =>            
                this._userService
                    .login(data.userName, data.password)
                    .pipe(tap(()=>this._state.set({loading:true})))                    
                    .pipe(
                        catchError((err: { statusCode: string }) =>
                            of({ statusCode: err.statusCode, token: '' })
                        )
                    )
            )
        );
        this._state.connect(handler$, (prev, curr) => ({
            ...prev,
            statusCode: curr.statusCode,
            hasError: !curr.token,
            token: curr.token,
            loading:false
        }));

        this._state
            .select('token')
            .pipe(filter((x) => !!x))
            .subscribe({
                next: (token) => {
                    this._authService.persistToken(token);
                    this._router.navigate(['dashboard']);
                },
            });
    }
    showErrorViaMessages() {
        this.msgs = [];
        this.msgs.push({
            severity: 'error',
            summary: 'Error Message',
            detail: 'Validation failed',
        });
    }
    ngOnInit(): void {
        this.initForm();
        this.manageEvents();
        this.connectState();
    }
}
<div
    class="surface-ground flex align-items-center justify-content-center min-h-screen min-w-screen overflow-hidden"
>
    <div class="flex flex-column align-items-center justify-content-center">
        <img
            src="assets/LOGO-VSD.png"
            alt="Sakai logo"
            class="mb-5 w-6rem flex-shrink-0"
        />
        <div
            style="
                border-radius: 56px;
                padding: 0.3rem;
                background: linear-gradient(
                    180deg,
                    var(--primary-color) 10%,
                    rgba(33, 150, 243, 0) 30%
                );
            "
        >
            <div
                class="w-full surface-card py-8 px-5 sm:px-8"
                style="border-radius: 53px"
            >
                <div class="text-center mb-5">
                    <!-- <img
                        src="assets/demo/images/login/avatar.png"
                        alt="Image"
                        height="50"
                        class="mb-3"
                    /> -->
                    <div class="text-900 text-3xl font-medium mb-3">
                        Hệ thống quản lý công việc
                    </div>
                    <span class="text-600 font-medium">Đăng nhập hệ thống</span>
                </div>
                <ng-container *ngIf="state$ | async as vm">
                    <form [formGroup]="loginForm" (ngSubmit)="onSubmit.next()">
                        <p-messages
                            [(value)]="msgs"
                            [enableService]="false"
                            *ngIf="vm.hasError"
                        ></p-messages>
                        <div>
                            <label
                                for="email1"
                                class="block text-900 text-xl font-medium mb-2"
                                >Tên đăng nhập</label
                            >
                            <input
                                id="email1"
                                type="text"
                                placeholder="Tên đăng nhập"
                                pInputText
                                class="w-full md:w-30rem mb-5"
                                style="padding: 1rem"
                                formControlName="userName"
                            />

                            <label
                                for="password1"
                                class="block text-900 font-medium text-xl mb-2"
                                >Mật khẩu</label
                            >
                            <p-password
                                id="password1"
                                placeholder="Mật khẩu"
                                [toggleMask]="true"
                                styleClass="mb-5"
                                inputStyleClass="w-full p-3 md:w-30rem"
                                formControlName="password"
                            ></p-password>

                            <div
                                class="flex align-items-center justify-content-between mb-5 gap-5"
                            >
                                <div class="flex align-items-center">
                                    <p-checkbox
                                        id="rememberme1"
                                        [binary]="true"
                                        styleClass="mr-2"
                                    ></p-checkbox>
                                    <label for="rememberme1">Remember me</label>
                                </div>
                                <a
                                    class="font-medium no-underline ml-2 text-right cursor-pointer"
                                    style="color: var(--primary-color)"
                                    >Quên mật khẩu?</a
                                >
                            </div>
                            <button
                                pButton
                                [loading]="(loading$ | async)!"
                                pRipple
                                label="Đăng nhập"
                                class="w-full p-3 text-xl"
                                type="submit"
                            ></button>
                        </div>
                    </form>
                </ng-container>
            </div>
        </div>
    </div>
</div>
<div class="login-wrapper">
    <div class="container" id="container">
        <div class="form-container login-container">
            <form action="#">
                <h1>Login hire.</h1>
                <input type="email" placeholder="Email" />
                <input type="password" placeholder="Password" />
                <div class="content">
                    <div class="checkbox">
                        <input type="checkbox" name="checkbox" id="checkbox" />
                        <label>Remember me</label>
                    </div>
                    <div class="pass-link">
                        <a href="#">Forgot password?</a>
                    </div>
                </div>
                <button>Login</button>
            </form>
        </div>
        <div class="overlay-container">
            <div class="overlay">
                <div class="overlay-panel overlay-right"></div>
            </div>
        </div>
    </div>
</div>
